import React, { useState, useEffect } from "react";

function Create(props) {
    const [finalNote, setFinalNote] = useState({
        title: "",
        content: ""
    });

    function handleChange(event) {
        const { name, value } = event.target;
        setFinalNote((prev) => {
            return ({
                ...prev,
                [name]: value
            })
        });
    }
    function addItem(event) {
        console.log(finalNote);
        props.onUpdate(finalNote);
        event.preventDefault();
    }

    return <div>
        <form>
            <input name="title" type="text" placeholder="Title" onChange={handleChange} value={finalNote.title}></input>
            <input name="content" type="text" placeholder="Take A Note" onChange={handleChange} value={finalNote.content}></input>
            <button onClick={addItem}>Add</button>
        </form>

    </div>
}

export default Create;
