import React, { useState, useEffect } from "react";

function Create(props) {

    const [finalNote, setFinalNote] = useState({
        title: "",
        content: ""
    });
    useEffect(() => {
        // you will get updated finalData here, each time it changes
        console.log(finalNote);
        // you can trigger your function from here
    },[finalNote]);

    function handleChange(event) {
        const { name, value } = event.target;
        if (name === "title") {
            setFinalNote((prev) => {
                return ({
                    title: value,
                    content: prev.content
                })
            });
        }
        else {
            setFinalNote((prev) => {
                return ({
                    title: prev.title,
                    content: value
                })
            });
        }
       
        props.onAdd(finalNote);
    }
    function addItem() {
        console.log(finalNote);
        props.onUpdate(props.finalval);
    }

    return <div>
        <input name="title" type="text" placeholder="Title" onChange={handleChange}></input>
        <input name="content" type="text" placeholder="Take A Note" onChange={handleChange}></input>
        <button onClick={addItem}>Add</button>
    </div>
}

export default Create;
