import React, { useState } from "react";
import Heading from "./Header.jsx";
import Footer from "./Footer.jsx";
import Note from "./Note.jsx";
import Create from "./Create.jsx";

function App() {

    const [notes, setNotes] = useState([]);
    const [newNote, setNewNote] = useState("");

    function handleNewNote(val) {
        setNewNote(val);
        // console.log(val);
    }
    function addNewNote(val) {
        setNotes((prev) => {
            return ([...prev, val]);
        });
        setNewNote("");
        // console.log(notes);
    }

    return (<div><Heading></Heading>
        <Create onAdd={handleNewNote} onUpdate={addNewNote} finalval={newNote}></Create>
        {notes.map((note) => {
            return (<Note content={note.content} title={note.title}></Note>)
        })}
        <Footer></Footer></div>);
}

export default App;
