import React, { useState, useEffect } from "react";
import AddCircleIcon from '@material-ui/icons/AddCircle';
import Fab from '@material-ui/core/Fab';
import TextField from '@material-ui/core/TextField';

function Create(props) {
    const [finalNote, setFinalNote] = useState({
        title: "",
        content: ""
    });

    function handleChange(event) {
        const { name, value } = event.target;
        setFinalNote((prev) => {
            return ({
                ...prev,
                [name]: value
            })
        });
    }
    function addItem(event) {
        props.onUpdate(finalNote);
        setFinalNote({
            title: "",
            content: ""
        })
        event.preventDefault();
    }

    return <div>
        <form>
            <input name="title" type="text" placeholder="Title" onChange={handleChange} value={finalNote.title}></input>
            <input name="content" type="text" placeholder="Take A Note" onChange={handleChange} value={finalNote.content}></input>
            <Fab onClick={addItem}>
                <AddCircleIcon></AddCircleIcon>
            </Fab>
        </form>

    </div>
}

export default Create;
